def kth_smallest_element(list1, list2, k):
    # Pastikan k berada dalam rentang yang valid
    if not 1 <= k <= len(list1) + len(list2):
        raise ValueError("k berada di luar rentang yang valid.")
    def kth_in_sorted_arrays(A, B, k):
        # Fungsi pembantu/helper untuk menemukan elemen ke-k melalui pencarian biner

        # Fungsi helper (pembantu) untuk mendapatkan elemen di A dengan aman
        def A_elem(index):
            if 0 <= index < len(A):
                return A[index]
            return float('inf')

        # Fungsi helper (pembantu) untuk mendapatkan elemen di B dengan aman
        def B_elem(index):
            if 0 <= index < len(B):
                return B[index]
            return float('inf')

        lo, hi = 0, k
        while lo < hi:
            midA = lo + (hi - lo) // 2
            midB = k - midA - 2
            A_mid = A_elem(midA)
            B_mid = B_elem(midB)
            A_next = A_elem(midA + 1)
            B_next = B_elem(midB + 1)

            if A_mid <= B_next and B_mid <= A_next:
                return max(A_mid, B_mid)
            if A_mid > B_next:
                hi = midA
            else:
                lo = midA + 1

        return min(A_elem(lo), B_elem(k - lo - 2))

    return kth_in_sorted_arrays(list1, list2, k)

