                for (Row row : sheet) {
                    // Pastikan row atau baris bukan nol dan memiliki setidaknya dua kolom
                    if (row != null && row.getLastCellNum() >= 2) {
                        Cell cell = row.getCell(1); // Mengakses sel di kolom kedua
                        if (cell != null && cell.getCellType() == CellType.NUMERIC) {
                            totalSum += cell.getNumericCellValue();
                        }
                    }
                }
            }
        } catch (IOException e) {
            System.err.println("Error membaca file Excel: " + e.getMessage());
