#!/usr/bin/perl
use strict;
use warnings;

sub find_max_value {
     my @array = @_;

     # Kiểm tra xem mảng có trống không
     return "mảng trống" unless @array;

     # Giá trị mặc định là giá trị đầu tiên của mảng.
     my $max_value = $array[0];

     # Vòng lặp tìm giá trị lớn nhất
foreach my $num (@array) {
        if ($num > $max_value) {
            $max_value = $num;
        }
    }
    return $max_value;
}

