// ฟังก์ชั่นหาค่าฟีโบนัชชีตัวที่ n
int fibonacci(int n) {
    if (n <= 0) {
        return 0;
    } else if (n == 1) {
        return 1;
    } else {
        int a = 0, b = 1, fib = 0;
        for (int i = 2; i <= n; i++) {
            fib = a + b; // คำนวณค่าฟีโบนัชชีปัจจุบัน
            a = b;       // เปลี่ยนค่า a เป็นค่าก่อนหน้าของ b
            b = fib;     // เปลี่ยนค่า b เป็นค่าฟีโบนัชชีปัจจุบัน
        }
        return fib;
    }
}

