def findMaxHeight(ironBars):
    totalSum = sum(ironBars)
    n = len(ironBars)
   
    # เริ่มต้นตาราง DP
    dp = [[False] * (totalSum // 2 + 1) for _ in range(n + 1)]
   
    # กรณีฐาน
    for i in range(n + 1):
        dp[i][0] = True

    # เพิ่มค่าในตาราง DP
    for i in range(1, n + 1):
        for j in range(1, totalSum // 2 + 1):
            if j < ironBars[i - 1]:
                dp[i][j] = dp[i - 1][j]
            else:
                dp[i][j] = dp[i - 1][j] or dp[i - 1][j - ironBars[i - 1]]

    # ค้นหาความสูงสูงสุดที่เป็นไปได้
    for j in range(totalSum // 2, -1, -1):
        if dp[n][j]:
            return j
    return 0

